import React, { useState, useEffect } from "react";
import axios from "axios";

function Home() {
  const [pokemons, setPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const fetchPokemons = async () => {
      const offset = (currentPage - 1) * 20;
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon?offset=${offset}&limit=20`);
      setPokemons(response.data.results);
    };
    fetchPokemons();
  }, [currentPage]);

  const handlePreviousPage = () => {
    if (currentPage > 1) setCurrentPage((prev) => prev - 1);
  };

  const handleNextPage = () => {
    setCurrentPage((prev) => prev + 1);
  };

  return (
    <div className="home">
      <h2>Página Principal</h2>
      {/* Mantén el diseño original de los Pokémon */}
      <div className="pokemon-container">
        {pokemons.map((pokemon, index) => (
          <div className="pokemon" key={index}>
            <p>{pokemon.name}</p>
            <img
              src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${index + 1 + (currentPage - 1) * 20}.png`}
              alt={pokemon.name}
            />
          </div>
        ))}
      </div>
      {/* Agregamos la funcionalidad de paginación */}
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Anterior
        </button>
        <span>Página {currentPage}</span>
        <button onClick={handleNextPage}>Siguiente</button>
      </div>
    </div>
  );
}

export default Home;
