import React from "react";

const PokemonCard = ({ pokemon }) => {
  const { name, id, sprites, types } = pokemon;

  // Función para agregar a favoritos
  const addToFavorites = () => {
    // Obtener favoritos del localStorage o inicializar con un array vacío
    const favorites = JSON.parse(localStorage.getItem("favorites")) || [];

    // Verificar si el Pokémon ya está en favoritos
    if (!favorites.find((fav) => fav.id === id)) {
      favorites.push(pokemon);  // Agregar el Pokémon a la lista
      localStorage.setItem("favorites", JSON.stringify(favorites));  // Guardar en localStorage
    }
  };

  return (
    <div className="pokemon-card">
      <img src={sprites.front_default} alt={name} />
      <h3>{name}</h3>
      <p>Tipos: {types.map((type) => type.type.name).join(", ")}</p>
      <button onClick={addToFavorites}>Agregar a Favoritos</button>
    </div>
  );
};

export default PokemonCard;
