import React from "react";
import { useHistory } from "react-router-dom"; // Para redirigir al detalle del Pokémon

function PokemonCard({ pokemon }) {
  const history = useHistory();

  // Función para manejar la adición a favoritos
  const handleAddToFavorites = () => {
    const existingFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
    // Verificar si el Pokémon ya está en favoritos
    if (!existingFavorites.some((fav) => fav.name === pokemon.name)) {
      existingFavorites.push(pokemon);
      localStorage.setItem("favorites", JSON.stringify(existingFavorites));
    }
  };

  // Función para redirigir al detalle del Pokémon
  const handleGoToDetails = () => {
    history.push(`/pokemon/${pokemon.name}`);
  };

  return (
    <div className="pokemon-card">
      <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${pokemon.url.split("/")[6]}.png`} alt={pokemon.name} />
      <h3>{pokemon.name}</h3>
      <button onClick={handleGoToDetails}>Ver Detalles</button>
      <button onClick={handleAddToFavorites}>Agregar a Favoritos</button>
    </div>
  );
}

export default PokemonCard;
