import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonCard from "../components/PokemonCard"; // Componente para mostrar la tarjeta de cada Pokémon

const Home = () => {
  const [pokemons, setPokemons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const pokemonsPerPage = 20;

  useEffect(() => {
    // Función para obtener los Pokémon
    const fetchPokemons = async () => {
      setLoading(true);
      setError(null);
      const offset = (currentPage - 1) * pokemonsPerPage;

      try {
        const response = await axios.get(
          `https://pokeapi.co/api/v2/pokemon?limit=${pokemonsPerPage}&offset=${offset}`
        );
        const allPokemons = await Promise.all(
          response.data.results.map(async (pokemon) => {
            const pokemonDetails = await axios.get(pokemon.url);
            return pokemonDetails.data;
          })
        );
        setPokemons(allPokemons); // Aquí almacenamos los Pokémon completos con todos los detalles
      } catch (err) {
        setError("Error al cargar los Pokémon");
      } finally {
        setLoading(false);
      }
    };

    fetchPokemons();
  }, [currentPage]);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setCurrentPage(1);  // Regresar a la página 1 cuando se realice una búsqueda
  };

  // Filtrar Pokémon por nombre
  const filteredPokemons = pokemons.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Cambiar página
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Buscar Pokémon"
        value={searchTerm}
        onChange={handleSearch}
      />
      {loading ? (
        <p>Cargando Pokémon...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div>
          <div className="pokemon-list">
            {filteredPokemons.length > 0 ? (
              filteredPokemons.map((pokemon) => (
                <PokemonCard key={pokemon.id} pokemon={pokemon} />
              ))
            ) : (
              <p>No se encontraron Pokémon con ese nombre.</p>
            )}
          </div>
          <div className="pagination">
            <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
              Anterior
            </button>
            <span>Página {currentPage}</span>
            <button onClick={() => handlePageChange(currentPage + 1)}>
              Siguiente
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
