import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/main.css";  // Asegúrate de que este archivo de estilo está correcto

function PokemonDetail() {
  const { name } = useParams();
  const [pokemon, setPokemon] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPokemonDetail = async () => {
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${name}`);
      setPokemon(response.data);
    };
    fetchPokemonDetail();
  }, [name]);

  if (!pokemon) return <p>Cargando...</p>;

  return (
    <div className="pokemon-detail">
      <button onClick={() => navigate("/")} className="back-button">Volver</button>
      <div className="detail-content">
        <img
          src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.id}.png`}
          alt={pokemon.name}
        />
        <h1>{pokemon.name}</h1>
        <p>Altura: {pokemon.height / 10} m</p>
        <p>Peso: {pokemon.weight / 10} kg</p>
        
        <div className="pokemon-stats">
          <h3>Habilidades</h3>
          <ul>
            {pokemon.abilities.map((ability) => (
              <li key={ability.ability.name}>{ability.ability.name}</li>
            ))}
          </ul>

          <h3>Tipos</h3>
          <ul>
            {pokemon.types.map((type) => (
              <li key={type.type.name} className="pokemon-type">
                {type.type.name}
              </li>
            ))}
          </ul>

          <h3>Estadísticas</h3>
          <div className="stat-container">
            {pokemon.stats.map((stat) => (
              <div key={stat.stat.name} className="stat-item">
                <p>{stat.stat.name}: <strong>{stat.base_stat}</strong></p>
                <div className="stat-bar">
                  <div
                    className="stat-bar-fill"
                    style={{ width: `${stat.base_stat}%` }}
                  ></div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default PokemonDetail;
