import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonCard from "../components/PokemonCard";
import "../styles/main.css";

function Home() {
  const [pokemons, setPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");

  // Obtener Pokémon según la página actual
  useEffect(() => {
    const fetchPokemons = async () => {
      setLoading(true);
      try {
        const offset = (currentPage - 1) * 20; // Calcular el offset
        const response = await axios.get(
          `https://pokeapi.co/api/v2/pokemon?offset=${offset}&limit=20`
        );

        const detailedPokemons = await Promise.all(
          response.data.results.map(async (pokemon) => {
            const details = await axios.get(pokemon.url); // Obtener datos detallados
            return {
              name: pokemon.name,
              url: pokemon.url,
              types: details.data.types.map((t) => t.type.name), // Extraer tipos
              id: details.data.id, // Guardar el ID para la imagen
            };
          })
        );
        setPokemons(detailedPokemons);
      } catch (error) {
        console.error("Error al obtener los Pokémon:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchPokemons();
  }, [currentPage]);

  // Manejar el cambio de página
  const handlePreviousPage = () => {
    if (currentPage > 1) setCurrentPage((prev) => prev - 1);
  };

  const handleNextPage = () => {
    setCurrentPage((prev) => prev + 1);
  };

  // Filtrar Pokémon por búsqueda
  const filteredPokemons = pokemons.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="home">
      <h2>Página Principal</h2>
      <input
        type="text"
        placeholder="Buscar Pokémon"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="search-bar"
      />
      <div className="pokemon-container">
        {loading ? (
          <p>Cargando Pokémon...</p>
        ) : filteredPokemons.length > 0 ? (
          filteredPokemons.map((pokemon, index) => (
            <PokemonCard key={index} pokemon={pokemon} />
          ))
        ) : (
          <p>No se encontraron Pokémon que coincidan con la búsqueda</p>
        )}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Anterior
        </button>
        <span>Página {currentPage}</span>
        <button onClick={handleNextPage} disabled={filteredPokemons.length < 20}>
          Siguiente
        </button>
      </div>
    </div>
  );
}

export default Home;
