import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/main.css";

const Home = () => {
  const [pokemonList, setPokemonList] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);

  const fetchPokemonList = async (page = 1) => {
    const limit = 20;
    const offset = (page - 1) * limit;

    try {
      const response = await axios.get(
        `https://pokeapi.co/api/v2/pokemon?limit=${limit}&offset=${offset}`
      );
      const results = await Promise.all(
        response.data.results.map(async (pokemon) => {
          const details = await axios.get(pokemon.url);
          return details.data;
        })
      );
      setPokemonList(results);
    } catch (error) {
      console.error("Error fetching Pokémon:", error);
    }
  };

  const addToFavorites = (pokemon) => {
    const storedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
    const isFavorite = storedFavorites.some((fav) => fav.id === pokemon.id);

    if (!isFavorite) {
      const updatedFavorites = [
        ...storedFavorites,
        {
          id: pokemon.id,
          name: pokemon.name,
          image: pokemon.sprites.front_default,
          types: pokemon.types.map((type) => type.type.name),
        },
      ];
      localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
      alert(`${pokemon.name} ha sido agregado a favoritos.`);
    } else {
      alert(`${pokemon.name} ya está en favoritos.`);
    }
  };

  useEffect(() => {
    fetchPokemonList(currentPage);
  }, [currentPage]);

  const handleSearch = () => {
    if (!searchTerm.trim()) {
      fetchPokemonList(currentPage);
      return;
    }
    const filtered = pokemonList.filter((pokemon) =>
      pokemon.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setPokemonList(filtered);
  };

  return (
    <div className="home">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Buscar Pokémon"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Buscar</button>
      </div>
      <div className="pokemon-grid">
        {pokemonList.map((pokemon) => (
          <div key={pokemon.id} className="pokemon-card">
            <img src={pokemon.sprites.front_default} alt={pokemon.name} />
            <h3>{pokemon.name}</h3>
            <p>Tipo: {pokemon.types.map((type) => type.type.name).join(", ")}</p>
            <button onClick={() => addToFavorites(pokemon)}>Agregar a Favoritos</button>
          </div>
        ))}
      </div>
      <div className="pagination">
        <button onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}>
          Anterior
        </button>
        <button onClick={() => setCurrentPage((prev) => prev + 1)}>Siguiente</button>
      </div>
    </div>
  );
};

export default Home;
