import React, { useState, useEffect } from "react";

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const storedFavorites = JSON.parse(localStorage.getItem("favorites")) || [];
    setFavorites(storedFavorites);
  }, []);

  const removeFromFavorites = (pokemonId) => {
    const updatedFavorites = favorites.filter((fav) => fav.id !== pokemonId);
    setFavorites(updatedFavorites); // Actualiza el estado local
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites)); // Actualiza el localStorage
  };

  return (
    <div>
      <h1>Favoritos</h1>
      <div className="favorites-list">
        {favorites.length > 0 ? (
          favorites.map((pokemon) => (
            <div key={pokemon.id} className="pokemon-card">
              <img src={pokemon.image} alt={pokemon.name} />
              <h3>{pokemon.name}</h3>
              <p>Tipo: {pokemon.types.join(", ")}</p>
              <button
                onClick={() => removeFromFavorites(pokemon.id)}
                className="remove-button"
              >
                Eliminar
              </button>
            </div>
          ))
        ) : (
          <p>No tienes Pokémon en favoritos.</p>
        )}
      </div>
    </div>
  );
};

export default Favorites;
