import React, { useState, useEffect } from "react";
import axios from "axios";
import PokemonCard from "../components/PokemonCard";
import "../styles/main.css";

function Home() {
  const [pokemons, setPokemons] = useState([]);
  const [allPokemons, setAllPokemons] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [search, setSearch] = useState("");

  // Obtener todos los Pokémon de la API
  useEffect(() => {
    const fetchPokemons = async () => {
      const response = await axios.get("https://pokeapi.co/api/v2/pokemon?limit=1000");  // Obtener todos los Pokémon
      setPokemons(response.data.results);
      setAllPokemons(response.data.results);  // Guardar todos los Pokémon
    };
    fetchPokemons();
  }, []);

  // Filtrar los Pokémon según la búsqueda
  useEffect(() => {
    const filtered = allPokemons.filter((pokemon) =>
      pokemon.name.toLowerCase().includes(search.toLowerCase())
    );
    setPokemons(filtered);
    setCurrentPage(1); // Restablecer la página a 1 después de una búsqueda
  }, [search, allPokemons]);

  // Manejar el cambio de página
  const handlePreviousPage = () => {
    if (currentPage > 1) setCurrentPage((prev) => prev - 1);
  };

  const handleNextPage = () => {
    setCurrentPage((prev) => prev + 1);
  };

  // Dividir los Pokémon filtrados en páginas
  const pokemonsPerPage = 20;
  const indexOfLastPokemon = currentPage * pokemonsPerPage;
  const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage;
  const currentPokemons = pokemons.slice(indexOfFirstPokemon, indexOfLastPokemon);

  return (
    <div className="home">
      <h2>Página Principal</h2>
      <input
        type="text"
        placeholder="Buscar Pokémon"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="search-bar"
      />
      <div className="pokemon-container">
        {currentPokemons.length > 0 ? (
          currentPokemons.map((pokemon, index) => (
            <PokemonCard key={index} pokemon={pokemon} />
          ))
        ) : (
          <p>No se encontraron Pokémon que coincidan con la búsqueda</p>
        )}
      </div>
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          Anterior
        </button>
        <span>Página {currentPage}</span>
        <button onClick={handleNextPage}>Siguiente</button>
      </div>
    </div>
  );
}

export default Home;
