import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages/Home';
import Favorites from './pages/Favorites';
import Navbar from './components/Navbar';

const App = () => {
    const [favorites, setFavorites] = useState([]);
    const pokemons = [/* Aquí van tus datos de los Pokémon, por ejemplo: 
                        { id: 1, name: 'bulbasaur', image: 'url', type: 'grass' }, 
                        ... */];

    const addToFavorites = (pokemon) => {
        setFavorites([...favorites, pokemon]);
    };

    const removeFromFavorites = (pokemon) => {
        setFavorites(favorites.filter(fav => fav.id !== pokemon.id));
    };

    return (
        <Router>
            <Navbar />
            <Routes>
                <Route path="/" element={<Home pokemons={pokemons} addToFavorites={addToFavorites} />} />
                <Route path="/favorites" element={<Favorites favorites={favorites} removeFromFavorites={removeFromFavorites} />} />
            </Routes>
        </Router>
    );
};

export default App;
